mapper.update:
update cm_site set name=#{name}, domain=#{domain}, other_domain=#{otherDomain}, template=#{template}, mobile_template=#{mobile_template}
, language=#{language}, default_site=#{default_site}, editor=#{editor}, editor_name=#{editorName}, edit_time=now()
where id=#{id} and tenant_id=#{tenantId}

mapper.select:
select * from cm_site a where tenant_id=#{tenantId}
and ( a.name like concat('%', #{searchValue}, '%') or a.domain like concat('%', #{searchValue}, '%') or a.other_domain like concat('%', #{searchValue}, '%') or a.template like concat('%', #{searchValue}, '%') or a.mobile_template like concat('%', #{searchValue}, '%') )
order by CONVERT(${order.key} USING gbk) ${order.value}
order by create_time
