mapper.select:
select a.*, m1.display_name creator_name, m2.display_name editor_name from us_product a left join us_manager m1 on a.creator=m1.id left join us_manager m2 on a.editor=m2.id
a.name like concat('%', #{searchValue}, '%') or a.code like concat('%', #{searchValue}, '%') or a.release_url like concat('%', #{searchValue}, '%') or a.product_manager like concat('%', #{searchValue}, '%') or a.version like concat('%', #{searchValue}, '%')
order by CONVERT(${order.key} USING gbk) ${order.value}

select a.*, m1.display_name creator_name, m2.display_name editor_name from us_product a
left join us_manager m1 on a.creator=m1.id left join us_manager m2 on a.editor=m2.id
where a.id=#{id}

mapper.update:
update us_product set name=#{name}, code=#{code}, version=#{version}, release_url=#{releaseUrl}, release_time=#{releaseTime},
product_manager=#{productManager}, note=#{note}, editor=#{editor}, edit_time=now()
where id=#{id}
