mapper.select:
select a.*, m1.display_name creator_name, m2.display_name editor_name from us_tenant a left join us_manager m1 on a.creator=m1.id left join us_manager m2 on a.editor=m2.id
a.name like concat('%', #{searchValue}, '%') or a.code like concat('%', #{searchValue}, '%') or a.contact like concat('%', #{searchValue}, '%') or a.mobile like concat('%', #{searchValue}, '%') or a.phone like concat('%', #{searchValue}, '%') or a.email like concat('%', #{searchValue}, '%')
order by CONVERT(${order.key} USING gbk) ${order.value}

select a.*, m1.display_name creator_name, m2.display_name editor_name from us_tenant a
left join us_manager m1 on a.creator=m1.id left join us_manager m2 on a.editor=m2.id
where a.id = #{id}

mapper.update:
update us_tenant set name=#{name}, code=#{code}, contact=#{contact}, mobile=#{mobile}, phone=#{phone}, email=#{email}
, address=#{address}, logo=#{logo}, note=#{note}, editor=#{editor}, edit_time=now()
where id=#{id}
